mapper.select:
select * from ( select M.*, rownum as rn from ( select userid, passwd, name, email, gender, hp, zipcode, addr1, addr2, quit, join_date, quit_date from member_tb
where ${searchOp.searchOption1} like '%'||#{searchOp.searchKeyword}||'%'
where userid like '%'||#{searchOp.searchKeyword}||'%' or name like '%'||#{searchOp.searchKeyword}||'%' or addr1 like '%'||#{searchOp.searchKeyword}||'%'
and join_date between to_date(#{searchOp.startDate}, 'yyyy-mm-dd') and to_date(#{searchOp.endDate}, 'yyyy-mm-dd')+1
order by join_date desc
order by ${searchOp.orderOption}
) M ) where rn between #{start} and #{end}
and quit='n'
and quit='y'

select * from (
	select Q.*, rownum as rn from (
		select
		q.bno, q.writer, m.name, q.title, q.content, q.reg_date, q.viewcnt,
		q.reply_state, q.show
		from qna_tb q, member_tb m
		where q.writer=#{userid}
		and m.userid=#{userid}
		order by q.reg_date desc
	) Q
) where rn <= 6

select * from (
		select R.*, rownum as rn from (
		select no, r.userid, e.code, r.res_date, 
		r.adult, r.teen, r.kids, r.quantity,
		r.sub_total, r.pay, m.name, e.title
		from reserv_item_tb r, member_tb m, exhibition_tb e
		where r.userid=m.userid and r.code=e.code and r.userid=#{userid}
		order by r.res_date desc
	) R
	) where rn <= 6

mapper.update:
update member_tb set quit='y', quit_date=#{today}
where

userid=#{list}

mapper.select:
select * from ( select E.*, rownum as rn from ( select code, location, gallery, title, summary, thumnail, start_date, end_date, hit, sales from exhibition_tb
where ${searchOp.searchOption1} like '%'||#{searchOp.searchKeyword}||'%'
where code like '%'||#{searchOp.searchKeyword}||'%' or title like '%'||#{searchOp.searchKeyword}||'%' or location like '%'||#{searchOp.searchKeyword}||'%' or gallery like '%'||#{searchOp.searchKeyword}||'%'
and ( start_date between to_date(#{searchOp.startDate}, 'yyyy-mm-dd') and to_date(#{searchOp.endDate}, 'yyyy-mm-dd')+1 or end_date between to_date(#{searchOp.startDate}, 'yyyy-mm-dd') and to_date(#{searchOp.endDate}, 'yyyy-mm-dd')+1 )
order by ${searchOp.orderOption} desc
order by ${searchOp.orderOption}
) E ) where rn between #{start} and #{end}

mapper.update:
update exhibition_tb set
	location=#{location}, gallery=#{gallery}, title=#{title},
	summary=#{summary}, thumnail=#{thumnail}, 
	start_date=#{start_date}, end_date=#{end_date}
where code=#{code}

mapper.select:
select * from ( select R.*, rownum as rn from ( select no, r.userid, e.code, r.res_date, r.adult, r.teen, r.kids, r.quantity, r.sub_total, e.title, e.gallery, r.r_name, r.pay, r.r_addr1 from reserv_item_tb r, exhibition_tb e
where ${searchOp.searchOption1} like '%'||#{searchOp.searchKeyword}||'%'
where r.no like '%'||#{searchOp.searchKeyword}||'%' or r.userid like '%'||#{searchOp.searchKeyword}||'%' or e.code like '%'||#{searchOp.searchKeyword}||'%' or e.title like '%'||#{searchOp.searchKeyword}||'%'
and res_date between to_date(#{searchOp.startDate}, 'yyyy-mm-dd') and to_date(#{searchOp.endDate}, 'yyyy-mm-dd')+1
order by #{searchOp.orderOption} desc
order by ${searchOp.orderOption}
) R ) where rn between #{start} and #{end}
and pay='y'
and pay='n'
