mapper.select:
SELECT * FROM ( SELECT M.*, rownum as rn FROM ( SELECT userid, passwd, name, email, gender, hp, zipcode, addr1, addr2, quit, join_date, quit_date FROM member_tb
WHERE ${searchOp.searchOption1} like '%'||#{searchOp.searchKeyword}||'%'
WHERE (userid like '%'||#{searchOp.searchKeyword}||'%' or name like '%'||#{searchOp.searchKeyword}||'%' OR addr1 like '%'||#{searchOp.searchKeyword}||'%')
AND join_date between to_date(#{searchOp.startDate}, 'yyyy-mm-dd') AND TO_DATE(#{searchOp.endDate}, 'yyyy-mm-dd')+1
ORDER BY join_date desc
ORDER BY ${searchOp.orderOption}
) M ) WHERE rn between #{start} AND #{end}
AND quit='n'
AND quit='y'

SELECT * FROM (
SELECT Q.*, rownum as rn FROM (
SELECT
q.bno, q.writer, m.name, q.title, q.content, q.reg_date, 
q.viewcnt, q.reply_state, q.show
FROM qna_tb q, member_tb m
WHERE q.writer=#{userid} AND m.userid=#{userid}
ORDER BY q.reg_date desc
) Q
) WHERE rn <= 6

mapper.update:
update member_tb SET quit='y', quit_date=#{today}
WHERE

	userid=#{list}

mapper.select:
SELECT * FROM ( SELECT E.*, rownum as rn FROM ( SELECT code, location, gallery, title, summary, thumnail, start_date, end_date, hit, sales, show FROM exhibition_tb
WHERE ${searchOp.searchOption1} like '%'||#{searchOp.searchKeyword}||'%'
WHERE (code like '%'||#{searchOp.searchKeyword}||'%' OR title like '%'||#{searchOp.searchKeyword}||'%' OR location like '%'||#{searchOp.searchKeyword}||'%' OR gallery like '%'||#{searchOp.searchKeyword}||'%')
AND show='y'
AND show='n'
AND ( (start_date between to_date(#{searchOp.startDate}, 'yyyy-mm-dd') AND TO_DATE(#{searchOp.endDate}, 'yyyy-mm-dd')+1) OR (end_date between TO_DATE(#{searchOp.startDate}, 'yyyy-mm-dd') AND to_date(#{searchOp.endDate}, 'yyyy-mm-dd')+1) )
ORDER BY ${searchOp.orderOption}
ORDER BY ltrim(title)
ORDER BY ${searchOp.orderOption} desc
) E ) WHERE rn between #{start} AND #{end}

mapper.insert:
INSERT INTO product_info_tb (code, attach, content)
values (
	(SELECT code FROM exhibition_tb WHERE code=#{code}), 
	#{attach}, #{content}
	)

mapper.update:
UPDATE exhibition_tb SET
location=#{location}, gallery=#{gallery}, title=#{title}, summary=#{summary}, 
thumnail=#{thumnail}, start_date=#{start_date}, end_date=#{end_date}, 
adult_price=#{adult_price}, teen_price=#{teen_price}, kids_price=#{kids_price}
WHERE code=#{code}

UPDATE product_info_tb SET
attach=#{attach}, content=#{content}
WHERE code=#{code}

BEGIN 

UPDATE exhibition_tb SET thumnail='-'
WHERE code=#{code};


UPDATE product_info_tb SET attach='-'
WHERE code=#{code};

END;

mapper.select:
SELECT * FROM ( SELECT I.*, rownum as rn FROM ( SELECT r.no, r.userid, r.code, r.res_date, r.adult, r.teen, r.kids, r.quantity, r.sub_total, e.title, e.gallery, r.r_name, r.pay, r.r_addr1, r.show, r.reg_date FROM reserv_item_tb r, exhibition_tb e WHERE r.code=e.code
AND ${searchOp.searchOption1} like '%'||#{searchOp.searchKeyword}||'%'
AND (r.no like '%'||#{searchOp.searchKeyword}||'%' or r.userid like '%'||#{searchOp.searchKeyword}||'%' OR r.code like '%'||#{searchOp.searchKeyword}||'%' OR e.title like '%'||#{searchOp.searchKeyword}||'%')
AND (r.reg_date between to_date(#{searchOp.startDate}, 'yyyy-mm-dd') AND to_date(#{searchOp.endDate}, 'yyyy-mm-dd')+1) AND r.show='y'
ORDER BY ${searchOp.orderOption} desc
ORDER BY ${searchOp.orderOption}
) I ) WHERE rn between #{start} AND #{end}
AND pay='y'
AND pay='n'

SELECT * FROM ( SELECT Q.*, rownum as rn FROM ( SELECT bno, writer, title, m.name, content, q.reg_date, viewcnt, reply_state, q.show FROM qna_tb q, member_tb m WHERE q.writer=m.userid
AND ${searchOp.searchOption1} like '%'||#{searchOp.searchKeyword}||'%'
AND (bno like '%'||#{searchOp.searchKeyword}||'%' OR writer like '%'||#{searchOp.searchKeyword}||'%' OR title like '%'||#{searchOp.searchKeyword}||'%' OR content like '%'||#{searchOp.searchKeyword}||'%')
AND (q.reg_date between to_date(#{searchOp.startDate}, 'yyyy-mm-dd') AND to_date(#{searchOp.endDate}, 'yyyy-mm-dd')+1) AND q.show='y'
ORDER BY ${searchOp.orderOption}
ORDER BY ${searchOp.orderOption} desc
) Q ) WHERE rn between #{start} AND #{end}
AND reply_state='y'
AND reply_state='n'

SELECT * FROM ( SELECT P.*, rownum as rn FROM ( SELECT no, title, img_src, img_width, img_height, pos_width, pos_height, filename, filesize, start_date, end_date, reg_date, show FROM popup_tb
WHERE ${searchOp.searchOption1} like '%'||#{searchOp.searchKeyword}||'%'
WHERE (no like '%'||#{searchOp.searchKeyword}||'%' OR title like '%'||#{searchOp.searchKeyword}||'%' OR filename like '%'||#{searchOp.searchKeyword}||'%')
AND show='y'
AND show='n'
AND (start_date between to_date(#{searchOp.startDate}, 'yyyy-mm-dd') AND to_date(#{searchOp.endDate}, 'yyyy-mm-dd')+1 OR end_date between to_date(#{searchOp.startDate}, 'yyyy-mm-dd') AND to_date(#{searchOp.endDate}, 'yyyy-mm-dd')+1)
ORDER BY ${searchOp.orderOption}
ORDER BY ${searchOp.orderOption} desc
) P ) WHERE rn between #{start} AND #{end}
